$(document).ready(function(){$("body").fadeIn(400),$(".thumbnail").mouseenter(function(){$(this).children(".zoomTool").fadeIn()}),$(".thumbnail").mouseleave(function(){$(this).children(".zoomTool").fadeOut()}),$(window).scroll(function(){$(this).scrollTop()>200?$(".gotop").fadeIn(200):$(".gotop").fadeOut(200)}),$(".gotop").click(function(e){e.preventDefault(),$.scrollTo("#gototop",1500,{easing:"easeOutCubic"})})}),$(document).on("click","#subscription",function(){if(email=document.getElementById("newsletter_email").value,""==email)alert("\u8acb\u8f38\u5165\u90f5\u4ef6\u5594\u5594\u5594");else{var e="/consoles/subscription?email="+email;$.ajax({url:e,type:"get",data:$(this).serialize(),dataType:"text"}).done(function(e){alert((e="success")?"\u8a02\u95b1\u6210\u529f":"\u8a02\u95b1\u5931\u6557")}).fail(function(){alert("\u6b64email\u5df2\u8a02\u95b1")})}});